        const info = {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(newTopic),
        };
        console.log(info);
        const response = await fetch("http://localhost:8000/api", info);
        addNewTopic(response.data);
      } catch (err) {
        console.log(err);
      }
    }
  }

  const addNewTopic = topic => setTopics(topics => [topics, ...topic]);
  // function deleteTopic(id) {
