    // Using React-Router for Home/Session Pages
    <div className='App'>
      <Router>
        <Header className='Header' />
        <div className='App-Body'>
          <Switch>
            <TopicContext.Provider
              value={{
                // deleteTopic,
                createTopic,
                // inputChangeHandler,
                // isTopicClicked,
                // setIsTopicClicked,
                // submitTopicTitleChange,
                topics,
                topicInputHandler,
              }}
            >
              <Route exact path='/'>
                <div>
                  <ContentBodyParent
                    className='Topic-Child-Container'
                    createTopic={createTopic}
                    getTopics={getTopics}
                    input={topicInput}
                    topicInputHandler={topicInputHandler}
                  />
                </div>
              </Route>
              <Route exact path='/discussion'>
                <DiscussionSession />
              </Route>
            </TopicContext.Provider>
          </Switch>
        </div>
      </Router>
    </div>
  );
}
