import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { v4 as uuidv4 } from "uuid";
import axios from "axios";
import "./Styles/App.scss";
import "./Styles/Reset.css";
import "./Styles/Header.scss";
import "./Styles/ContentBody.scss";
import Header from "./components/Header.js";
import ContentBodyParent from "./components/ContentBodyParent.js";
import DiscussionSession from "./components/DiscussionSession.js";
import { TopicContext } from "./components/Contexts/TopicContext";

export default function App() {
  // State
  const [topics, setTopics] = useState([{ id: 1, topicTitle: "test" }]);
  const [topicInput, setTopicInput] = useState("");
  const [inputChange, setInputChange] = useState("");
  const [isTopicClicked, setIsTopicClicked] = useState(false);

  useEffect(() => {
    getTopics();
  }, []);

  // Functions;
  async function getTopics() {
    try {
      const response = await axios.get("http://localhost:8000/api/topics");
      const topics = await response.data;
      setTopics(topics.Items);
      console.log(response);
      console.log(topics);
    } catch (err) {
      console.log("Did not fetch topics: ", err);
    }
  }

  function topicInputHandler(e) {
    console.log(e);
    setTopicInput(e.target.value);
  }

  async function createTopic() {
    const newTopic = {
      id: uuidv4().toString(),
      topicTitle: topicInput,
    };
    setTopicInput("");
    if (topicInput === "") {
      alert("Field must not be empty.");
    } else {
      try {
        console.log(newTopic);
        const info = {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(newTopic),
        };
        await fetch("http://localhost:8000/api", info);
      } catch (err) {
        console.log(err);
      }
    }
  }

  function deleteTopic(id) {
    try {
      axios.delete(`http://localhost:8000/api/${id}`);
    } catch (err) {
      console.log(err);
    }
  }

  function inputChangeHandler(e) {
    setInputChange(e.target.value);
  }

  async function submitTopicTitleChange(id) {
    if (inputChange === "") {
      alert("Field must not be empty.");
    } else {
      try {
        const updatedTopic = { topicTitle: inputChange };
        const info = {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(updatedTopic),
        };
        await fetch(`http://localhost:8000/api/topic/${id}`, info);
      } catch (err) {
        console.log(err);
      }
      changeTopicClickedToFalse();
    }
  }

  function changeTopicClickedToFalse() {
    setIsTopicClicked(false);
  }

  return (
    // Using React-Router for Home/Session Pages
    <div className='App'>
      <Router>
        <Header className='Header' />
        <div className='App-Body'>
          <Switch>
            <TopicContext.Provider
              value={{
                deleteTopic,
                createTopic,
                inputChangeHandler,
                isTopicClicked,
                setIsTopicClicked,
                submitTopicTitleChange,
                topics,
                topicInputHandler,
              }}
            >
              <Route exact path='/'>
                <div>
                  <ContentBodyParent
                    className='Topic-Child-Container'
                    createTopic={createTopic}
                    getTopics={getTopics}
                    input={topicInput}
                    topicInputHandler={topicInputHandler}
                  />
                </div>
              </Route>
              <Route exact path='/discussion'>
                <DiscussionSession />
              </Route>
            </TopicContext.Provider>
          </Switch>
        </div>
      </Router>
    </div>
  );
}
