import React, { useState, useEffect } from "react";

export default function Timer() {
  const [seconds, setSeconds] = React.useState(10);

  useEffect(() => {
    if (seconds > 0) {
      setTimeout(() => setSeconds(seconds - 1), 1000);
    } else {
      setSeconds("BOOOOM!");
    }
  }, [seconds]);

  return (
    <div className='App'>
      <div>{seconds}</div>
    </div>
  );
}
