import React, { useState } from "react";
import "../Styles/Body.scss";
import TileContent from "./TileContent.js";

export default function Body() {
  const [topics, setTopics] = useState([
    { id: 1, topicTitle: "talk about react", user: "Steve" },
  ]);
  const [isButtonClicked, setIsButtonClicked] = useState(false);
  const [input, setInput] = useState("");
  const [isSubmitClicked, setIsSubmitClicked] = useState(false);

  function openTopicSubmitHandler() {
    setIsButtonClicked(true);
    console.log("Open Topic Handler Working");
  }
  function inputTopicHandler(e) {
    setInput(e.target.value);
    console.log("inputTopicHandler is sending input to state: ", input);
  }
  function submitTopicHandler() {
    const newArr = [{ id: Date.now(), topicTitle: input }, ...topics];
    setTopics(newArr);
    setIsSubmitClicked(true);
    setInput("");
    setIsButtonClicked(false);
    console.log("pressed submitTopicHandler");
  }

  return (
    <div className='Body-Container'>
      <div className='Body-Wrapper'>
        <div className='Title-Text-Wrapper'>
          <h2>Create & Vote On Topics</h2>
          <h3> Add Details Here </h3>
        </div>
        <div className='Content-Wrapper'>
          {topics.map(topic => {
            return <TileContent key={topic.id} />;
          })}

          {/* Conditionals based on whether or not a button/input was used. */}
          {isButtonClicked && (
            <div>
              <input value={input} onInput={inputTopicHandler}></input>
              <button onClick={submitTopicHandler}>Submit</button>
            </div>
          )}
          {!isButtonClicked && (
            <div>
              <button onClick={openTopicSubmitHandler}>+</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
