import React, { useState } from "react";

export default function TileContent({
  topic,
  changedTopicTitle,
  submitTopicTitleChange,
  isEditButtonClicked,
  editButtonClickedIsTrue,
}) {
  // State

  // Functions

  return (
    <div key={topic.id}>
      {!isEditButtonClicked ? (
        <div>
          <p onClick={editButtonClickedIsTrue}>{topic.topicTitle}</p>
        </div>
      ) : (
        <div>
          <input onInput={changedTopicTitle}></input>
          <button onClick={submitTopicTitleChange}>Submit Change</button>
        </div>
      )}
    </div>
  );
}
