import React, { useContext, useState } from "react";
import { TopicContext } from "../Contexts/TopicContext";
import Column from "./Column.js";
import { DragDropContext } from "react-beautiful-dnd";

export default function DragandDropContainer() {
  const { topics, setTopics } = useContext(TopicContext);
  const [initialData, setInitialData] = useState([
    {
      columns: {
        "column-1": {
          id: "column-1",
          title: "To do",
          taskIds: [topics.topicTitle],
        },
      },
      columnOrder: ["column-1"],
    },
  ]);

  // const discussionTopics = topics.map(topic => {
  //   return { id: topic.id, title: topic.topicTitle };
  // });

  // console.log(initialData);

  function onDragEnd(result) {
    const { destination, source, draggableId } = result;
    if (!destination) {
      return;
    }
    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      return;
    }

    const column = initialData.columns[source.droppableId];
    const newTaskIds = Array.from(column.taskIds);
    newTaskIds.splice(source.index, 1);
    newTaskIds.splice(destination.index, 0, draggableId);

    const newColumn = {
      ...column,
      taskIds: newTaskIds,
    };

    const newState = {
      ...initialData,
      columns: {
        ...initialData.columns,
        [newColumn.id]: newColumn,
      },
    };
    setInitialData(newState);
  }

  return (
    <div>
      <DragDropContext onDragEnd={onDragEnd}>
        {initialData.columnOrder.map(columnId => {
          const column = initialData.columns[columnId];
          const tasks = column.taskIds.map(taskId => taskId);
          // console.log("column", column);
          // console.log(tasks);
          return <Column key={column.id} column={column} tasks={tasks[0]} />;
        })}
      </DragDropContext>
    </div>
  );
}
