import React, { useState } from "react";
import "../Styles/Body.scss";
import TileContent from "./TileContent.js";
import Timer from "./Timer/Timer.js";

export default function Body({
  topics,
  changedTopicTitle,
  submitTopicTitleChange,
  input,
  setInput,
  createTopicHandler,
  setUpdatedTopicInput,
  updatedTopicInput,
  setIsEditButtonClicked,
  isEditButtonClicked,
  editButtonClickedIsTrue,
  getTopics,
}) {
  // State

  const [isTimerClicked, setIsTimerClicked] = useState(false);

  //Functions

  function inputTopicHandler(e) {
    setInput(e.target.value);
  }

  function openTimer() {
    setIsTimerClicked(true);
  }

  // Creating topic being sent to the database.
  async function createTopicHandler() {
    const newTopic = {
      id: Date.now().toString(),
      topicTitle: input,
    };
    if (input === "") {
      alert("Field must not be empty.");
    } else {
      const info = {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newTopic),
      };
      await fetch("http://localhost:8000/api", info);
    }
    getTopics();
  }

  return (
    <div className='Body-Container'>
      <div className='Body-Wrapper'>
        <div className='Title-Text-Wrapper'>
          <h1 className='Add-Topics-Header'>Add Topics</h1>
          <div className='Input-and-Button'>
            <input
              className='input'
              value={input}
              onInput={inputTopicHandler}
            ></input>
            <button className='submit-button' onClick={createTopicHandler}>
              Submit
            </button>
          </div>
        </div>
        <div className='Content-Wrapper'>
          {topics.map(topic => {
            return (
              <TileContent
                key={topic.id}
                topic={topic}
                id={topic.id}
                changedTopicTitle={changedTopicTitle}
                submitTopicTitleChange={submitTopicTitleChange}
                updatedTopicInput={updatedTopicInput}
                setUpdatedTopicInput={setUpdatedTopicInput}
                setIsEditButtonClicked={setIsEditButtonClicked}
                isEditButtonClicked={isEditButtonClicked}
                editButtonClickedIsTrue={editButtonClickedIsTrue}
              />
            );
          })}
        </div>
        {!isTimerClicked ? (
          <div onClick={openTimer}>CLICK HERE</div>
        ) : (
          <Timer />
        )}
      </div>
    </div>
  );
}
