import React, { useState, useEffect } from "react";
import "./Styles/App.scss";
import ContentBody from "./components/ContentBody.js";
import "./Styles/Reset.css";

export default function App() {
  // State
  const [topics, setTopics] = useState([]);
  const [topicInput, setTopicInput] = useState("");

  useEffect(() => {
    getTopics();
    // return () => {
    //   cleanup
    // }
  }, []);

  // Functions;
  function getTopics() {
    try {
      fetch("http://localhost:8000/api/topics")
        .then(res => res.json())
        .then(res => {
          setTopics(res.Items);
        });
    } catch (err) {
      console.log("Did not fetch topics!!", err);
    }
  }

  function topicTitleInputHandler(e) {
    setTopicInput(e.target.value);
  }

  async function createTopic() {
    const newTopic = {
      id: Date.now().toString(),
      topicTitle: topicInput,
    };
    if (topicInput === "") {
      alert("Field must not be empty.");
    } else {
      console.log(newTopic);
      const info = {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newTopic),
      };
      await fetch("http://localhost:8000/api", info);
    }
    getTopics();
  }

  return (
    <div className='App'>
      <div>
        <h1>Input Topic Below</h1>
        <input></input>
        <button>Submit</button>
        {topics.map(topic => (
          <div key={topic.id}>
            <ContentBody id={topic.id} topic={topic} />
          </div>
        ))}
      </div>
    </div>
  );
}
