import React, { useState } from "react";

export default function TileContent({
  topic,
  changedTopicTitle,
  submitTopicTitleChange,
  updatedTopicInput,
  setUpdatedTopicInput,
}) {
  // State
  const [isTopicClicked, setIsTopicClicked] = useState(false);
  // Functions

  return (
    <div>
      {!isTopicClicked ? (
        <div>
          <p>{topic.topicTitle}</p>
          <button onClick={() => setIsTopicClicked(true)}>Edit Topic</button>
        </div>
      ) : (
        <div>
          <input onInput={changedTopicTitle}></input>
          <button>Submit Change</button>
        </div>
      )}
    </div>
  );
}
