import React, { useState, useEffect } from "react";

export default function Tiles({ topics, setTopics }) {
  // State
  const [isButtonClicked, setIsButtonClicked] = useState(false);
  const [input, setinput] = useState("");
  const [isSubmitTopicClicked, setisSubmitTopicClicked] = useState(false);
  useEffect(() => {});
  // Functions
  function openTopicSubmitHandler() {
    setIsButtonClicked(true);
    console.log("Open Topic Handler Working");
  }
  function inputTopicHandler(e) {
    setinput(e.target.value);
    console.log("inputTopicHandler is sending input to state: ", input);
  }
  function submitTopicHandler() {
    const newArr = [{ id: Date.now(), topicTitle: input }, ...topics];
    setTopics(newArr);

    setisSubmitTopicClicked(true);
    setinput("");
    console.log("pressed submitTopicHandler");
  }

  return (
    <div>
      {/* {isSubmitTopicClicked && (
        <div>
          {topic.map(t => (
            <div key={t.id}>{t.topicTitle}</div>
          ))}
        </div>
      )} */}
      <>
        {!isButtonClicked ? (
          <button onClick={openTopicSubmitHandler}>+</button>
        ) : (
          <div>
            <input value={input} onInput={inputTopicHandler}></input>
            <button onClick={submitTopicHandler}>Submit</button>
          </div>
        )}
      </>
    </div>
  );
}
