import React, { useContext, useState } from "react";
import { TopicContext } from "../Contexts/TopicContext";
import Column from "./Column.js";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";
import { v4 as uuidv4 } from "uuid";

const som = [
  { id: uuidv4(), content: "asdf" },
  {
    id: uuidv4(),
    content: "lose",
  },
];

export default function DragandDropContainer() {
  const { topics, setTopics } = useContext(TopicContext);
  console.log(topics);
  const columnsFromTheBackend = {
    [uuidv4()]: {
      title: "column1",
      items: topics,
    },
  };
  console.log(columnsFromTheBackend);

  const [columns, setColumns] = useState(columnsFromTheBackend);

  return (
    <div>
      <DragDropContext>
        {Object.entries(columns).map(([id, column]) => {
          return (
            <Droppable droppableId={id}>
              {(provided, snapshot) => {
                return (
                  <div
                    {...provided.droppableProps}
                    ref={provided.innerRef}
                    style={{
                      background: snapshot.isDraggingOver
                        ? "lightblue"
                        : "lightgrey",
                      padding: 4,
                      width: 250,
                      minHeight: 500,
                    }}
                  >
                    {column.items.map((item, index) => {
                      return (
                        <Draggable
                          key={item.id}
                          draggableId={item.id}
                          index={index}
                        >
                          {(provided, snapshot) => {
                            return (
                              <div
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                                style={{
                                  userSelect: "none",
                                  padding: 16,
                                  margin: "0 0 8px 0",
                                  minHeight: "50px",
                                  backgroundColor: snapshot.isDragging
                                    ? "#263B4A"
                                    : "#456C86",
                                  color: "white",
                                  ...provided.draggableProps.style,
                                }}
                              >
                                {item}
                              </div>
                            );
                          }}
                        </Draggable>
                      );
                    })}
                  </div>
                );
              }}
            </Droppable>
          );
        })}
      </DragDropContext>
    </div>
  );
}
