import React, { useContext, useState } from "react";
import { TopicContext } from "../Contexts/TopicContext";
import Column from "./Column.js";
import { DragDropContext, Droppable } from "react-beautiful-dnd";
import { v4 as uuidv4 } from "uuid";
import styled from "styled-components";

const Container = styled.div`
  margin: 8px;
  border: 1px solid lightgrey;
  border-radius: 2px;
  color: white;
`;
//  const Title = styled.h3`
//   padding: 8px;
// `;
const TaskList = styled.div`
  background: snapshot.isDraggingOver ? 'lightblue' :  'lightgrey';
  padding: 4;
  width: 250, 
  minHeight: 500
`;
// const Container = styled.div`
//   border: 1px solid lightgrey;
//   border-radius: 2px;
//   padding: 8px;
//   margin-bottom: 8px;
//   background-color: grey;
// `;

export default function DragandDropContainer() {
  const { topics, setTopics } = useContext(TopicContext);

  const columnsFromTheBackend = {
    [uuidv4()]: {
      title: "column1",
      items: topics,
    },
  };
  console.log(columnsFromTheBackend);

  const [columns, setColumns] = useState(columnsFromTheBackend);

  return (
    <div>
      <Container>
        <DragDropContext>
          {Object.entries(columns).map(([id, column]) => {
            return (
              <Droppable droppableId={id}>
                {(provided, snapshot) => {
                  return (
                    <TaskList>
                      <div
                        {...provided.droppableProps}
                        ref={provided.innerRef}
                      ></div>
                    </TaskList>
                  );
                }}
              </Droppable>
            );
          })}
        </DragDropContext>
      </Container>
    </div>
  );
}
