import React, { useEffect, useState } from "react";
import "./Styles/App.scss";
import LeftSidebar from "./components/LeftSidebar.js";
import RightSidebar from "./components/RightSidebar.js";
import Body from "./components/Body.js";

export default function App() {
  // State
  const [topics, setTopics] = useState([]);
  const [input, setInput] = useState("");
  const [updatedTopicInput, setUpdatedTopicInput] = useState("");
  const [isEditButtonClicked, setIsEditButtonClicked] = useState(false);
  //Functions

  //Fetching data to show use the topic titles.
  useEffect(() => {
    getTopics();
  }, []);

  function getTopics() {
    try {
      fetch("http://localhost:8000/api/topics")
        .then(res => res.json())
        .then(res => {
          setTopics(res.Items);
        });
    } catch (err) {
      console.log("frontend did not fetch topics", err);
    }
  }

  // Creating topic being sent to the database.
  async function createTopicHandler() {
    const newTopic = {
      id: Date.now().toString(),
      topicTitle: input,
    };
    if (input === "") {
      alert("Field must not be empty.");
    } else {
      const info = {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newTopic),
      };
      await fetch("http://localhost:8000/api", info);
    }
    getTopics();
  }
  function changedTopicTitle(e) {
    setUpdatedTopicInput(e.target.value);
  }

  async function submitTopicTitleChange() {
    try {
      const updatedTopicTitle = { topicTitle: updatedTopicInput };
      const info = {
        method: "POST",
        header: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedTopicTitle),
      };
      await fetch(`http://localhost:8000/api/topics/${topics.id}`, info);
    } catch (err) {
      console.log(err);
    }
    getTopics();
    setIsEditButtonClicked(false);
  }

  function editButtonClickedIsTrue() {
    setIsEditButtonClicked(true);
  }
  return (
    <div className='App'>
      <LeftSidebar className='App-Left-Sidebar' />
      <Body
        className='App-Body'
        topics={topics}
        input={input}
        setInput={setInput}
        createTopicHandler={createTopicHandler}
        changedTopicTitle={changedTopicTitle}
        submitTopicTitleChange={submitTopicTitleChange}
        updatedTopicInput={updatedTopicInput}
        setUpdatedTopicInput={setUpdatedTopicInput}
        setIsEditButtonClicked={setIsEditButtonClicked}
        isEditButtonClicked={isEditButtonClicked}
        editButtonClickedIsTrue={editButtonClickedIsTrue}
      />
      <RightSidebar className='App-Right-Sidebar' />
    </div>
  );
}
